import CopyAllIcon from "@mui/icons-material/CopyAll";
import { Box, IconButton, Input, InputAdornment } from "@mui/material";
import Modal from "@mui/material/Modal";
import { SxProps, useTheme } from "@mui/material/styles";
import Typography from "@mui/material/Typography";

interface ShareLobbyModalProps {
  lobbyId: string;
  opened: boolean;
  handleCloseModal(): void;
}
const ShareLobbyModal = ({
  lobbyId,
  opened,
  handleCloseModal,
}: ShareLobbyModalProps): JSX.Element => {
  const theme = useTheme();
  const style: SxProps = {
    display: "flex",
    flexDirection: "column",
    position: "absolute" as "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    borderRadius: "26px",
    width: 400,
    bgcolor: theme.modal?.bgColor,
    boxShadow: 24,
    px: 4,
    py: 2,
  };
  const textFieldSx: SxProps = {
    margin: "18px 0",
    ".MuiInputLabel-outlined": {
      color: theme.common.text.secondary + " !important",
    },
    input: {
      color: theme.common.text.secondary,
    },
    fieldset: {
      borderColor: theme.textInput?.borderColor,
      "&.MuiOutlinedInput-notchedOutline": {
        borderColor: theme.textInput?.borderColor + " !important",
      },
    },
  };

  return (
    <>
      <Modal open={opened} onClose={handleCloseModal}>
        <Box sx={style}>
          <Typography
            id="modal-modal-title"
            variant="h6"
            component="h2"
            textAlign="center"
            color={theme.common.text.secondary}
          >
            Share this URL to your friends!
          </Typography>
          <Input
            autoComplete="off"
            sx={textFieldSx}
            id="outlined-basic"
            aria-readonly
            defaultValue={lobbyId}
            endAdornment={
              <InputAdornment position="end">
                <IconButton>
                  <CopyAllIcon />
                </IconButton>
              </InputAdornment>
            }
            readOnly
          />
        </Box>
      </Modal>
    </>
  );
};

export default ShareLobbyModal;
