import { gql } from "@apollo/client";

export const SEND_MESSAGE = gql`
  mutation ($addMessageInput: AddMessageInput) {
    addMessage(addMessageInput: $addMessageInput) {
      code
      success
      localDateSent
      message {
        message
        from {
          username
          id
        }
        to {
          id
        }
      }
    }
  }
`;

export const UPDATE_VIDEO = gql`
  mutation updateVideoStatus($statusInput: VideoStatusInput) {
    updateVideoStatus(statusInput: $statusInput) {
      code
      success
    }
  }
`;

export const MESSAGE_ADDED_SUBSCRIPTION = gql`
  subscription MessageAdded($lobbyId: ID!, $userId: ID!) {
    messageAdded(lobbyId: $lobbyId, userId: $userId) {
      lobbyId
      messages {
        from {
          id
          username
          type
        }
        message
        date
      }
    }
  }
`;
