import { Menu, Switch, SxProps } from "@mui/material";
import MenuItem from "@mui/material/MenuItem";
import { useTheme } from "@mui/material/styles";
import { useContext } from "react";
import { UsrContxt } from "../../App";

interface NavBarMenuProps {
  menuEl: HTMLElement | null;
  handleClose(): void;
  openChangeUsernameModal(): void;
  openChangeVideoModal(): void;
}

function NavBarMenu({
  menuEl,
  handleClose,
  openChangeUsernameModal,
  openChangeVideoModal,
}: NavBarMenuProps): JSX.Element {
  const userContext = useContext(UsrContxt);
  const theme = useTheme();

  const menuStyle: SxProps = {
    transform: "translateY(36px)",
    maxWidth: "300px",
    ".MuiPaper-root": {
      background: theme.modal?.bgColor,
      color: theme.common.text.secondary,
    },
  };

  const menuItemStyle: SxProps = {
    "&:hover": {
      backgroundColor: "rgba(0, 0, 0, 0.20)",
    },
  };
  return (
    <Menu
      sx={menuStyle}
      anchorEl={menuEl}
      anchorOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      transformOrigin={{
        vertical: "top",
        horizontal: "right",
      }}
      open={Boolean(menuEl)}
      onClose={handleClose}
    >
      <MenuItem sx={menuItemStyle}>
        Dark Mode
        <Switch
          inputProps={{ "aria-label": "Dark Mode Toggle" }}
          checked={userContext.darkMode}
          onClick={userContext.darkModeToggle}
        ></Switch>
      </MenuItem>
      <MenuItem sx={menuItemStyle} onClick={openChangeUsernameModal}>
        Change Username
      </MenuItem>
      <MenuItem sx={menuItemStyle} onClick={openChangeVideoModal}>
        Change Video
      </MenuItem>
    </Menu>
  );
}

export default NavBarMenu;
